import React, { useState, useEffect, Suspense } from "react";
import axios from "axios";
const LazyLoaded = React.lazy(() => import("./LazyLoaded"));

function View(props) {
  const { screen, setScreen } = props;
  const [box, showBox] = useState(false);
  const [data, setData] = useState();

  const deleteCookie = async () => {
    try {
      await axios.get("/clear-cookie");
      setScreen("auth");
    } catch (e) {
      console.log(e);
    }
  };

  const getData = async () => {
    try {
      const res = await axios.get("/get-data");
      console.log(res.data);
      setData(res.data);
    } catch (e) {
      console.log(e);
    }
  };

  return (
    <div>
      <Suspense fallback={<div>Loading...</div>}>
        {box ? <LazyLoaded /> : null}
      </Suspense>
      <p>{screen}</p>
      <p>{data}</p>
      <button onClick={getData}>Get Data</button>
      <button onClick={() => showBox(!box)}>Show Red box</button>
      <button onClick={deleteCookie}>Logout</button>
    </div>
  );
}

function App() {
  const [screen, setScreen] = useState("auth");
  const [username, setUsername] = useState();
  const [password, setPassword] = useState();

  const auth = async () => {
    try {
      const res = await axios.get("/authenticate", {
        auth: { username, password },
      });

      if (res.data.screen !== undefined) {
        setScreen(res.data.screen);
      }
    } catch (e) {
      console.log(e);
    }
  };

  const readCookie = async () => {
    try {
      const res = await axios.get("/read-cookie");

      if (res.data.screen !== undefined) {
        setScreen(res.data.screen);
      }
    } catch (e) {
      setScreen("auth");
      console.log(e);
    }
  };

  useEffect(() => {
    readCookie();
  }, []);

  return (
    <div className="App">
      {screen === "auth" ? (
        <div>
          <label>Username: </label>
          <br />
          <input type="text" onChange={(e) => setUsername(e.target.value)} />
          <br />
          <label>Password: </label>
          <br />
          <input
            type="password"
            onChange={(e) => setPassword(e.target.value)}
          />
          <br />
          <button onClick={auth}>Login</button>
        </div>
      ) : (
        <View screen={screen} setScreen={setScreen} />
      )}
    </div>
  );
}

export default App;
